import Foundation
import Lightning

/// the qrCode or json generated by zapconnect. url is optional.
struct RemoteNodeConfigurationQRCode: Codable {
    let certificate: String
    let macaroon: Data
    let url: URL
    
    enum CodingKeys: String, CodingKey {
        case certificate = "c"
        case macaroon = "m"
        case url = "ip"
    }
    
    init?(json: String) {
        guard
            let data = json.data(using: .utf8),
            let remoteNodeConfiguration = try? JSONDecoder().decode(RemoteNodeConfigurationQRCode.self, from: data)
            else { return nil }
        self = remoteNodeConfiguration
    }
}

extension RemoteRPCConfiguration {
    init(qrCode: RemoteNodeConfigurationQRCode) {
        self.init(certificate: qrCode.certificate, macaroon: qrCode.macaroon, url: qrCode.url)
    }
}
